public static byte[] hexToByte(String str) {
	    byte[] bytes = new byte[str.length() / 2];
	    for(int i = 0; i < str.length() / 2; i++) {
	        String subStr = str.substring(i * 2, i * 2 + 2);
	        bytes[i] = (byte) Integer.parseInt(subStr, 16);
	    }
	    return bytes;
	}

